import requests


def query_conceptnet(word, language='en'):
    """
     ConceptNet API 。

    ：
        word (str): 。
        language (str): （ 'en' ）。

    ：
        dict: 。
    """
    url = f"http://api.conceptnet.io/c/{language}/{word}"
    print(url)
    try:
        #  GET 
        response = requests.get(url)
        response.raise_for_status()
        data = response.json()
        return data
    except requests.exceptions.RequestException as e:
        print(f"API : {e}")
        return {}


def extract_relationships(data, relation='RelatedTo'):
    """
    。

    ：
        data (dict):  ConceptNet API 。
        relation (str): （ 'IsA'）。

    ：
        list: 。
    """
    relationships = []
    for edge in data.get('edges', []):
        if edge['rel']['label'] == relation:
            target = (
                edge['end']['label']
                if edge['start']['label'].lower() == word.lower()
                else edge['start']['label']
            )
            relationships.append(target)
            print("RelationTo:",edge['surfaceText'])
        if edge['rel']['label'] == "IsA":
            print("ISA:",edge['surfaceText'])

    return relationships


# 
if __name__ == "__main__":
    word = "school"
    data = query_conceptnet(word)
    relationships = extract_relationships(data, relation='RelatedTo')
    print(f"'{word}'  RelatedTo : {relationships}")
